INSERT INTO `sets_global_motto` (`global_motto_id`, `author`, `motto`, `create_time`, `update_time`) VALUES
(1, '墨子', '言不信者，行不果。', 1592490089, 1592490089),
(2, '柳宗元', '攀援而登，箕踞而遨，则几数州之土壤，皆在衽席之下。', 1592490089, 1592490089),
(3, '帕利克', '美德大都包含在良好的习惯之内。', 1592490089, 1592490089),
(4, '奥斯特洛夫斯基', '真正的朋友应该说真话，不管那话多么尖锐。', 1592490089, 1592490089),
(5, '德奥弗拉斯多', '时间是一切财富中最宝贵的财富。', 1592490089, 1592490089),
(6, '拉罗什福科', '如果不是怕别人反感，女人决不会保持完整的严肃。', 1592490089, 1592490089),
(7, '罗素', '希望是坚韧的拐杖，忍耐是旅行袋，携带它们，人可以登上永恒之旅。', 1592490089, 1592490089),
(8, '苏霍姆林斯基', '真正的教育者不仅传授真理，而且向自己的学生传授对待真理的态度，激发他们对于善良事物受到鼓舞和钦佩的情感，对于邪恶事物的不可容忍的态度。', 1592490089, 1592490089),
(9, '马克思', '我们现在必须完全保持党的纪律，否则一切都会陷入污泥中。', 1592490089, 1592490089),
(10, '马克思', '在科学上没有平坦的大道，只有不畏劳苦沿着陡峭山路攀登的人，才有希望达到光辉的顶点。', 1592490089, 1592490089),
(11, '李塨', '农，助天地以生衣食者。', 1592490089, 1592490089),
(12, '姬旦', '民之所欲，天必从之。', 1592490089, 1592490089),
(13, '享利·凡·戴克', '要对生命感到喜悦，因为它给了你去爱的机会，去工作，去玩乐，并用能仰头看星星的机会。', 1592490089, 1592490089),
(14, '苏格拉底', '美色不常驻。', 1592490089, 1592490089),
(15, '梭洛', '德行善举是惟一不败的投资。', 1592490089, 1592490089),
(16, '赫塞', '有勇气承担命运，这才是英雄好汉。', 1592490089, 1592490089),
(17, '伏契克', '英雄就是这样一个人，他在决定性关头做了为人类社会的利益所需要的事。', 1592490089, 1592490089),
(18, '车尔尼雪夫斯基', '实践是思想的真理。', 1592490089, 1592490089),
(19, '爱献生', '在科学里，嘲笑腐儒，也就类似宗教里的嘲笑迷信。', 1592490089, 1592490089),
(20, '毛泽东', '天下者，得之艰难，则失之不易；', 1592490089, 1592490089),
(21, '季米里亚席夫', '对于科学，敌人比朋友还多。', 1592490089, 1592490089),
(22, '莎士比亚', '思想——自由的精灵。', 1592490089, 1592490089),
(23, '阿尔巴尼亚', '狐狸一念经，快赶饿入笼。', 1592490090, 1592490090),
(24, '斯特林堡', '生活的目的究竟是什么？是生活本身！除把生命维持到死以处不会再有其他任务。人因此而获得维持生命的力量。', 1592490090, 1592490090),
(25, '屠格涅夫', '科学的全部目的，就是有意识地取得大自然无代价的赋予青春的一切。', 1592490090, 1592490090),
(26, '海明威', '事非经过不知难。', 1592490090, 1592490090),
(27, '塞内加', '我可以明确地告诉你，许多人并未因此罪恶而受以，相反，还因此得到好处。', 1592490090, 1592490090),
(28, '马可·奥勒留', '你的心也许会破碎，但人们却依旧会像从前一样生活。', 1592490090, 1592490090),
(29, '培根', '得不到友谊的人将是终身可怜的孤独者。没有友情的社会则只是一片繁华的沙漠。', 1592490090, 1592490090),
(30, '张九龄', '相知无远近，万里尚为邻。', 1592490090, 1592490090),
(31, '罗曼·罗兰', '你们这些生在今日的人，你们这些青年，现在要轮到你们了！踏实在我们的身体上面向前吧。但愿你们比我们更伟大、更幸福。', 1592490090, 1592490090),
(32, '桑弗', '真正的朋友有三种：爱你的朋友，忘你的朋友，恨你的朋友。', 1592490090, 1592490090),
(33, '穆尼尔·纳素夫', '生活中，谅解可以产生奇迹，谅解可以挽回感情上的损失，谅解犹如一个火把，能照亮由焦躁、怨恨和复仇心理铺就的道路。', 1592490090, 1592490090),
(34, '埃斯库罗斯', '人不应该有高傲之心，高傲会开花，结成破灭之果。在收获的季节，会得到止不住的眼泪。', 1592490090, 1592490090),
(35, '乔·爱德华兹', '神圣之物不但手不可去玷污，而且。', 1592490090, 1592490090),
(36, '韩非子', '宰相之耳，待天下之心识而后能发。', 1592490090, 1592490090),
(37, '彼得·波特', '在宇宙的中心，回响着那个坚定神秘的音符：“我”', 1592490090, 1592490090),
(38, '杜荀鹤', '言论关时务，篇章见国风。', 1592490090, 1592490090),
(39, '乔叟', '酒杯中可见天堂，但纵情狂饮只能。', 1592490090, 1592490090),
(40, '萨迪', '温和、谦逊、多礼的言行，有时能使人回心转意。', 1592490090, 1592490090),
(41, '培根', '同情是一切道德中最高的美德。', 1592490090, 1592490090),
(42, '高尔基', '我扑到书籍上，就像饥饿的人扑在面包上。', 1592490090, 1592490090),
(43, '詹姆斯·格兰德', '批评不是一个趣味的问题，而且是一个谁的趣味的问题。', 1592490090, 1592490090),
(44, '康德', '良心是一种根据道德准则来判断自己的本能，它不只是一种能力；它是一种本能。', 1592490090, 1592490090),
(45, '司马迁', '规小节者不能成荣名，恶小耻者不能立大功。', 1592490090, 1592490090),
(46, '甘必大', '不善思索的有才能的人，必定以悲剧收场。', 1592490090, 1592490090),
(47, '麦考莱', '美貌是上帝的赠礼，但能炫耀美貌。', 1592490090, 1592490090),
(48, '杰拉尔德·李', '僵化的绝非机器，而是头脑机械的人。', 1592490090, 1592490090),
(49, '杜特斯', '仁慈和正义是并辔齐驱的。', 1592490090, 1592490090),
(50, '赫尔岑', '科学不是可以不劳而获的，诚然，在科学上除了汗流满面是没有其他获得成功的方法的；热情也罢，幻想也罢，以整个身心去渴望也罢，都不能代替劳动。', 1592490090, 1592490090),
(51, '马辛杰', '要明智些，扬之过高易落，屈尊方能求荣。', 1592490090, 1592490090),
(52, '爱因斯坦', '在真理和认识方面，任何以权威者自居的人，必将在上帝的戏笑中垮台！', 1592490090, 1592490090),
(53, '康同璧', '舍卫山河历劫尘，布金环殿三巡。若论女士西游者，我是支那第一人。', 1592490090, 1592490090),
(54, '李卡', '我们这一代就是施肥的一代，用自己的血灌溉快将实现的乐园，让后代享受人类应有的一切幸福，这就是我们一代的任务。', 1592490090, 1592490090),
(55, '契诃夫', '对自己不满足，是任何真正有天才的人的根本特征之一。', 1592490090, 1592490090),
(56, '李大钊', '本来善恶根源的不可解，就是宗教发生的一个原因。', 1592490090, 1592490090),
(57, '毕达哥拉斯', '要这样生活；使你的朋友不致成为仇人，使你的仇人却成为朋友。', 1592490090, 1592490090),
(58, '蒙田', '我需要三件东西：爱情友谊和图书。然而这三者之间何其相通！炽热的爱情可以充实图书的内容，图书又是人们最忠实的朋友。', 1592490090, 1592490090),
(59, '荀子', '以治气养生，则后彭祖；以修身自名，则配尧舜。', 1592490090, 1592490090),
(60, '朱熹', '虚心顺理，学者当守此四字。', 1592490090, 1592490090),
(61, '车尔尼雪夫斯基', '爱情赐予万事万物的魅力，其实决不应该是人生中短暂现象，这一道绚烂的生命的光芒，不应该仅仅照耀着探求和渴慕时期，这个时期其实只应该相当于一天的黎明，黎明虽然可爱，美丽，但在接踵而至的白天，那光和热却比黎明时分更大得多。', 1592490090, 1592490090),
(62, '爱默生', '不是真正的豪杰就无法成为真正的演说家。', 1592490090, 1592490090),
(63, '肖伯纳', '有机会出去的话人人都要离开监狱。', 1592490090, 1592490090),
(64, '洛克', '热爱真理的确实特征，是对任何一个命题的接受绝不超过其证据所显示的程度。', 1592490090, 1592490090),
(65, '罗·伯顿', '没有什么绳索能比爱情拧成的双股线更经拉，经拽。', 1592490090, 1592490090),
(66, '王粲', '人生各有志。', 1592490090, 1592490090),
(67, '拉斯金', '一个人专心于本身的时候，他充其量也只能成为一个美丽，小巧的包裹而已。', 1592490090, 1592490090),
(68, '韩非', '侈而惰者贫，而力而俭者富。', 1592490090, 1592490090),
(69, '雨果', '别人放手，他仍然坚持；别人后退，他仍然前冲；每次跌倒，立刻站起来--这种人一定没有失败。', 1592490090, 1592490090),
(70, '阿巴巴耶娃', '离别对于爱情，就像风对于火一样：它熄灭了火星，但却能煽起狂焰。', 1592490090, 1592490090),
(71, '郭小川', '但愿每次回忆，对生活都不感到负疚。', 1592490090, 1592490090),
(72, '里根', '任何一个孩子因死亡、离婚、被抛弃而失去父母，永远是个悲剧。有些单身家庭确实也尽到了照顾自己孩子的责任，但是多数美国人认为，单身这家庭并不是理想的模式。', 1592490090, 1592490090),
(73, '兰多尔夫·伯恩', '井掏三遍吃甜水，人从三师武艺高。', 1592490090, 1592490090),
(74, '普劳图斯', '他本想行善这句话毫无意义，除非他确实这样去做。', 1592490090, 1592490090),
(75, '许名奎', '凿壁偷光，聚萤作囊；忍贫读书，车胤匡衡。', 1592490090, 1592490090),
(76, '皮日休', '落尽残红始吐芳，佳名唤作百花王。竟夸天下无双艳，独占人间第一香。', 1592490090, 1592490090),
(77, '杨万里', '精华在笔端，咫只匠心难。', 1592490090, 1592490090),
(78, '布莱克', '那种一味期待而从不行动的人们，是滋生瘟疫的温床。', 1592490090, 1592490090),
(79, '克雷洛夫', '才能一旦让懒惰支配，它就一无可为。', 1592490090, 1592490090),
(80, '尼古拉·奥斯特洛夫斯基', '无论家庭或是爱情，都不能使人觉得生活真正美满。家庭，只是几个人；爱情，仅是一个人；而党，这是一百六十万人。只为家庭活着，这是禽兽的私心；只为一个人活着，这是卑鄙；只为自己活着，这是耻辱。', 1592490090, 1592490090),
(81, '茨威格', '女人拒绝异性的追求，是先天性的特权，即使拒绝了一个最热烈爱情，也不会被认为残酷，但是，如果命运女神乱了安排，让女人打破了羞怯的本性，不顾一切地向一个并无把握的异性献出她的热爱，而对方表示着冷淡和拒绝时，那结果就不堪设想了。男人拒绝女人的追求，等于损伤她的最高贵的自尊。', 1592490090, 1592490090),
(82, '苏武', '努力爱春华，莫忘欢乐时，生当复来归，死当长相思。', 1592490090, 1592490090),
(83, '池田大作', '即使是孩子，也有一个人格，也是一个独立的人，这个前提必须明确，孩子决不是父母的所有物，他的人格是构成社会的组成部分之一，这一个人格必须用充沛的爱来培养。', 1592490090, 1592490090),
(84, '莫洛亚', '良辰难再，人生中大好时刻，不要再去旧梦重圆。', 1592490090, 1592490090),
(85, '杜克洛克斯', '反复地推断，无休止地修正，就能在科学上取得勿容置疑的进步。', 1592490090, 1592490090),
(86, '郭熙', '春山淡冶而如笑，夏山苍翠而欲滴，秋山明净而如妆，冬山惨淡而如睡。', 1592490090, 1592490090),
(87, '布尔韦尔·利顿', '在科学著作中，你最好读最新的书，在文学著作中，你最好读最老的书。古典文学作品永远不会衰老。', 1592490090, 1592490090),
(88, '爱因斯坦', '在科学上，每一条道路都应该走一走，发现一条走不通的道路，就是对科学的一大贡献。……那种证明“此路不通”的吃力不讨好的工作，就让我来做吧！', 1592490090, 1592490090),
(89, '邓小平', '开放、搞活政策延续多久，端正党风的工作就得干多久，纠正不正之风，打击犯罪就得干多久，这样才能保证我们开放，搞活政策扔正确执行。', 1592490090, 1592490090),
(90, '皮萨列夫', '思想上的错误会引起语言上的错误，言论上的错误会引起行动上的错误。', 1592490090, 1592490090),
(91, '于谦', '春风秋月不相待，倏忽朱颜变白头。', 1592490090, 1592490090),
(92, '拿破仑', '勇气如爱情，需要希望来滋养。', 1592490090, 1592490090),
(93, '爱献生', '思想重复一百遍也还有独到之处。', 1592490090, 1592490090),
(94, '陶渊明', '盛年不重来，一日难再晨。及时宜自勉，岁月不待人。', 1592490090, 1592490090),
(95, '曹操', '冉冉老将至，何时返故乡？', 1592490090, 1592490090),
(96, '康普顿', '科学赐予人类的最大礼物是什么呢？是使人类相信真理的力量。', 1592490090, 1592490090),
(97, '亚里士多德', '我爱我的老师，但我更爱真理。', 1592490090, 1592490090),
(98, '崔颢', '昔人已乘黄鹤去，此地空余黄鹤楼。', 1592490090, 1592490090),
(99, '司汤达', '心灵上的谨慎和谦恭是惟一无二的美德。', 1592490090, 1592490090),
(100, '雨果', '精神象乳汁一样是可以养育的，智慧便是一只乳房。', 1592490090, 1592490090),
(101, '王尔德', '世界上只有一件事比被人议论还要糟糕，那就是不被议论。', 1592490090, 1592490090),
(102, '奥斯特洛夫斯基', '我只相信一条：灵感是在劳动的时候产生的。', 1592490090, 1592490090),
(103, '奥维德', '习惯是在习惯中养成的。', 1592490090, 1592490090),
(104, '莎士比亚', '迁延蹉跎，来日无多，二十丽姝，请来吻我，衰草枯杨，青春易过。', 1592490090, 1592490090),
(105, '黄庭坚', '海川风雨独凭栏，绾结湘娥十二鬟。', 1592490090, 1592490090),
(106, '佩利', '美德大多存在于良好的习惯中。', 1592490090, 1592490090),
(107, '塞·约翰逊', '最大限度地行使权力总是令人反感；权力不易确定之处始终存在着危险。', 1592490090, 1592490090),
(108, '顾况', '手提三尺龙泉剑，不斩奸邪誓不休。', 1592490090, 1592490090),
(109, '叔本华', '人们给同类施加痛苦并无其他原因，仅仅是出于恶意。在所有的动物中，唯有人这么做。', 1592490090, 1592490090),
(110, '陶行知', '处处是创造之地，天天是创造之时，人人是创造之人。', 1592490090, 1592490090),
(111, '管仲', '道德当身，不以物惑。', 1592490090, 1592490090),
(112, '拉罗什夫科', '人人都抱怨缺乏记忆力，但没有一个人抱怨缺乏健全的思想。', 1592490090, 1592490090),
