INSERT INTO `sets_global_motto` (`global_motto_id`, `author`, `motto`, `create_time`, `update_time`) VALUES
(2918, '杨朔', '作为一个人，要是不经历过人世上的悲欢离合，不跟生活打过交手仗，就不可能懂得人生的意义。', 1592490108, 1592490108),
(2919, '苏格拉底', '不要靠馈赠来获得一个朋友。你须贡献你挚情的爱，学习怎样用正当的方法来赢得一个人的心。', 1592490108, 1592490108),
(2920, '毛泽东', '懂得了全局性的东西，就更会使用局部性的东西，因为局部性的东西是隶属于全局性的东西的。', 1592490108, 1592490108),
(2921, '费尔巴哈', '宗教是人类精神之梦。', 1592490108, 1592490108),
(2922, '艾青', '即是我们是一支蜡烛也应该\"蜡烛成灰泪始干\"；即使我们只是一根火柴，也要在关键时刻有一次闪耀；即使我们死后尸骨都腐烂了，也要变成磷火在荒野中燃烧。', 1592490108, 1592490108),
(2923, '卢克莱修', '站在高岸上遥看颠簸于大海中的行船是愉快的，站在堡垒中遥看激战中的战场也是愉快的，但是没有能比攀登于真理的高峰之上，然后俯视来路上的层层迷障、烟雾和曲折更愉快了！', 1592490108, 1592490108),
(2924, '车尔尼雪夫欺基', '没有目标，哪来的劲头？', 1592490109, 1592490109),
(2925, '骚塞', '自私是万物之源泉。', 1592490109, 1592490109),
(2926, '桓宽', '王者行仁政，无敌于天下。', 1592490109, 1592490109),
(2927, '郑獬', '若比人间恶风浪，长江风浪本无心。', 1592490109, 1592490109),
(2928, '萨克雷', '我们怎样把现在这一系列评论小品写到第五十二篇，亲爱的读者和势利小人弟兄们，我趁早点说不上。但是在整整一年的时间里，我们都在一起议论人类咒骂人类了。即使我们能多活上一百年，关于势利小人这个大题目，我想大家也还有的是话可说呢。', 1592490109, 1592490109),
